curl --unix-socket /var/run/docker.sock "http://localhost/containers/chungus/stats?stream=true" | jq --unbuffered -c '{ time: .read, cpu_total: .cpu_stats.cpu_usage.total_usage, sys_total: .cpu_stats.system_cpu_usage, pre_cpu: .precpu_stats.cpu_usage.total_usage, pre_sys: .precpu_stats.system_cpu_usage, mem: .memory_stats.usage, cpus: .cpu_stats.online_cpus }' > resource_usage_lean.json 